      Thread.CurrentThread.CurrentCulture = cultureInfo;
      Thread.CurrentThread.CurrentUICulture = cultureInfo;
    }
    else if (frmLogin.CompilingLanguage == "CN")
    {
      CultureInfo cultureInfo = new CultureInfo("zh-Hans");
      Thread.CurrentThread.CurrentCulture = new CultureInfo("zh-CN");
      Thread.CurrentThread.CurrentUICulture = cultureInfo;
    }
    this.AutoScaleMode = AutoScaleMode.Dpi;
    this.InitializeComponent();
    frmMain.frmMainInstance = this;
    string str1 = $"{AppDomain.CurrentDomain.BaseDirectory}log\\{frmLogin.GAuto.Settings.UserLogFile}";
    if (new FileInfo(str1).Exists)
    {
      try
      {
        StreamReader streamReader = new StreamReader(str1);
        if (streamReader != null)
        {
          List<string> stringList = new List<string>();
          while (!streamReader.EndOfStream)
          {
            string str2 = streamReader.ReadLine();
            if (!string.IsNullOrEmpty(str2))
              stringList.Add(str2);
          }
          int num = 0;
          if (stringList.Count > 50)
            num = stringList.Count - 50;
          for (int index = num; index < stringList.Count; ++index)
            this.richLog.AppendText(stringList[index] + "\r\n");
          streamReader.Close();
          frmMain.frmMainInstance.richLog.SelectionStart = frmMain.frmMainInstance.richLog.Text.Length;
          frmMain.frmMainInstance.richLog.ScrollToCaret();
        }
      }
      catch (Exception ex)
      {
        this.richLog.AppendText(frmMain.langCannotreadlog);
      }
    }
    MyDLL.RegisterHotKey(this.Handle, 0, 6, Keys.Q.GetHashCode());
    this.KeyIDs.Add(0);
    MyDLL.RegisterHotKey(this.Handle, 1, 6, Keys.Down.GetHashCode());
    this.KeyIDs.Add(1);
    MyDLL.RegisterHotKey(this.Handle, 2, 6, Keys.F12.GetHashCode());
    this.KeyIDs.Add(2);
    MyDLL.RegisterHotKey(this.Handle, 3, 0, Keys.Pause.GetHashCode());
    this.KeyIDs.Add(3);
    MyDLL.RegisterHotKey(this.Handle, 4, 6, Keys.H.GetHashCode());
    this.KeyIDs.Add(4);
    MyDLL.RegisterHotKey(this.Handle, 5, 6, Keys.P.GetHashCode());
    this.KeyIDs.Add(5);
    MyDLL.RegisterHotKey(this.Handle, 6, 6, Keys.B.GetHashCode());
    this.KeyIDs.Add(6);
    MyDLL.RegisterHotKey(this.Handle, 7, 6, Keys.D.GetHashCode());
    this.KeyIDs.Add(7);
    MyDLL.RegisterHotKey(this.Handle, 8, 6, Keys.M.GetHashCode());
    this.KeyIDs.Add(8);
  }

  protected override void WndProc(ref Message m)
  {
    base.WndProc(ref m);
    if (m.Msg == 786)
    {
      int lparam1 = (int) m.LParam;
      int lparam2 = (int) m.LParam;
      switch (m.WParam.ToInt32())
      {
        case 0:
          if (this.lvAllAccounts.Items.Count <= 0)
            break;
          frmLogin.GAuto.Settings.flagEnableAuto = !frmLogin.GAuto.Settings.flagEnableAuto;
          bool flag = false;
          try
          {
            for (int index = 0; index < this.lvAllAccounts.Items.Count; ++index)
            {
              if (!frmLogin.GAuto.Settings.flagEnableAuto)
              {
                frmLogin.GAuto.AllAutoAccounts[index].IsAIEnabled = false;
                this.lvAllAccounts.Items[index].Checked = false;
              }
              else
              {
                frmLogin.GAuto.AllAutoAccounts[index].IsAIEnabled = true;
                this.lvAllAccounts.Items[index].Checked = true;
                flag = true;
              }
            }
            if (!flag)
            {
              this.notifyIcon.ShowBalloonTip(5000, frmMain.langTempSuspendAuto, frmMain.langSuspendAutoAllAccounts, ToolTipIcon.Info);
              break;
            }
            this.notifyIcon.ShowBalloonTip(5000, frmMain.langEnableAuto, frmMain.langEnableAutoAllAccounts, ToolTipIcon.Info);
            break;
          }
          catch (Exception ex)
          {
            break;
          }
        case 1:
          AutoAccount currentAccount = this.GetCurrentAccount();
          if (currentAccount != null)
            frmMain.CheckAndHideGame(currentAccount);
          this.Show();
          this.lvAllAccounts.Focus();
          break;
        case 2:
          if (!frmLogin.GAuto.Settings.ShowSupportLog)
          {
            int num = (int) new frmSupport().ShowDialog();
            break;
          }
          frmLogin.GAuto.Settings.ShowSupportLog = false;
          try
          {
            frmLogin.GAuto.Settings.IsSupportMode = false;
            break;
          }
          catch (Exception ex)
          {
            break;
          }
        case 3:
          frmLogin.GAuto.Settings.flagHideAuto = !frmLogin.GAuto.Settings.flagHideAuto;
          if (frmLogin.GAuto.Settings.flagHideAuto)
          {
            this.WindowState = FormWindowState.Minimized;
            this.Hide();
            break;
          }
          this.Show();
          this.WindowState = FormWindowState.Normal;
          break;
        case 4:
          if (frmLogin.GAuto.AllAutoAccounts.Count <= 0)
            break;
          frmLogin.GAuto.Settings.flagAnAllGame = !frmLogin.GAuto.Settings.flagAnAllGame;
          if (frmLogin.GAuto.Settings.flagAnAllGame)
          {
            try
            {
              for (int index = frmLogin.GAuto.AllAutoAccounts.Count - 1; index >= 0; --index)
                this.NVAnGame(frmLogin.GAuto.AllAutoAccounts[index]);
              break;
            }
            catch (Exception ex)
            {
              break;
            }
          }
          else
          {
            try
            {
              for (int index = frmLogin.GAuto.AllAutoAccounts.Count - 1; index >= 0; --index)
                this.NVHienGame(frmLogin.GAuto.AllAutoAccounts[index]);
              break;
            }
            catch (Exception ex)
            {
              break;
            }
          }
        case 5:
          if (frmLogin.GAuto.AllAutoAccounts.Count <= 0)
            break;
          frmLogin.GAuto.Settings.flagPetOn = !frmLogin.GAuto.Settings.flagPetOn;
          if (frmLogin.GAuto.Settings.flagPetOn)
          {
            this.NVGPetOn();
            break;
          }
          this.NVGPetOff();
          break;
        case 6:
          if (frmLogin.GAuto.AllAutoAccounts.Count <= 0)
            break;
          frmLogin.GAuto.Settings.flagNhatBoc = !frmLogin.GAuto.Settings.flagNhatBoc;
          if (frmLogin.GAuto.Settings.flagNhatBoc)
            this.NVGNhatBocOn();
          else
            this.NVGNhatBocOff();
          this.cboxTuNhatVatPham.Checked = frmLogin.GAuto.Settings.flagNhatBoc;
          break;
        case 7:
          if (frmLogin.GAuto.AllAutoAccounts.Count <= 0)
            break;
          frmLogin.GAuto.Settings.flagDanhQuai = !frmLogin.GAuto.Settings.flagDanhQuai;
          try
          {
            for (int index = frmLogin.GAuto.AllAutoAccounts.Count - 1; index >= 0; --index)
              frmLogin.GAuto.AllAutoAccounts[index].IsAIEnabled = frmLogin.GAuto.Settings.flagDanhQuai;
          }
          catch (Exception ex)
          {
          }
          if (frmLogin.GAuto.Settings.flagDanhQuai)
            this.notifyIcon.ShowBalloonTip(5000, frmMain.langTurnonHunting, frmMain.langTurnOnHuntingAllAccounts, ToolTipIcon.Info);
          else
            this.notifyIcon.ShowBalloonTip(5000, frmMain.langTurnOffHunting, frmMain.langTurnOffHuntingAllAccounts, ToolTipIcon.Info);
          this.cboxDanhQuai.Checked = frmLogin.GAuto.Settings.flagDanhQuai;
          break;
        case 8:
          if (frmLogin.GAuto.AllAutoAccounts.Count <= 0)
            break;
          frmLogin.GAuto.Settings.flagNMBuff = !frmLogin.GAuto.Settings.flagNMBuff;
          try
          {
            for (int index = frmLogin.GAuto.AllAutoAccounts.Count - 1; index >= 0; --index)
              frmLogin.GAuto.AllAutoAccounts[index].Settings.cboxNMBuff = frmLogin.GAuto.Settings.flagNMBuff;
          }
          catch (Exception ex)
          {
          }
          if (frmLogin.GAuto.Settings.flagNMBuff)
            this.notifyIcon.ShowBalloonTip(5000, frmMain.langTurnonLotusBuff, frmMain.langTurnonLotusBuffAll, ToolTipIcon.Info);
          else
            this.notifyIcon.ShowBalloonTip(5000, frmMain.langTurnoffLotusBuff, frmMain.langTurnoffLotusBuffAll, ToolTipIcon.Info);
          this.cboxNMBuff.Checked = frmLogin.GAuto.Settings.flagNMBuff;
          break;
      }
    }
    else if ((long) m.Msg == (long) frmLogin.GAuto.Settings.HAOTESTMSG)
    {
      int wparam = (int) m.WParam;
      int lparam = (int) m.LParam;
    }
    else
    {
      if ((long) m.Msg != (long) frmLogin.GAuto.Settings.WM_CRITDLL || !(m.WParam == (IntPtr) 1) || !(m.LParam != (IntPtr) 0))
        return;
      if (frmLogin.GAuto.AllAutoAccounts.Count <= 0)
        return;
      try
      {
        for (int index = frmLogin.GAuto.AllAutoAccounts.Count - 1; index >= 0; --index)
        {
          if (frmLogin.GAuto.AllAutoAccounts[index].Target.ProcessID == (int) m.LParam)
          {
            lock (frmLogin.GAuto.AllAutoAccounts[index].MyFlag.DLLReadyLock)
            {
              frmLogin.GAuto.AllAutoAccounts[index].MyFlag.DLLReady = true;
              break;
            }
          }
        }
      }
      catch (Exception ex)
      {
      }
    }
  }

