  public static void OpenNewGame(int amount = 1)
  {
    string str = "";
    string workingDir = "";
    bool flag = false;
    if (!string.IsNullOrEmpty(frmLogin.GAuto.Settings.ExeFilePath) && System.IO.File.Exists(frmLogin.GAuto.Settings.ExeFilePath))
    {
      str = frmLogin.GAuto.Settings.ExeFilePath;
      flag = true;
    }
    if (frmLogin.GAuto.AllAutoAccounts.Count == 0 && !flag)
    {
      if (str == string.Empty)
        str = frmMain.GetGamEXEPath();
      flag = true;
    }
    else if (frmLogin.GAuto.AllAutoAccounts.Count > 0 && !flag)
    {
      str = frmLogin.GAuto.AllAutoAccounts[0].Target.ProcessExePath;
      frmLogin.GAuto.Settings.ExeFilePath = str;
      flag = true;
    }
    if (!flag || !System.IO.File.Exists(str))
      return;
    frmMain.RunGameEXE(amount, str, workingDir);
  }

  public static string RunGameEXE(int amount, string fullPath, string workingDir)
  {
    bool flag = false;
    string str = "";
    using (MD5 md5 = MD5.Create())
    {
      using (FileStream inputStream = System.IO.File.OpenRead(fullPath))
        str = BitConverter.ToString(md5.ComputeHash((Stream) inputStream)).Replace("-", "").ToUpper();
    }
    if (str != "")
    {
      foreach (BaseInfo baseInfo in frmLogin.MyBases)
      {
        if (baseInfo.myHash == str && baseInfo.myProvider == "tk" && baseInfo.myVersion != "3.73")
        {
          flag = true;
          break;
        }
      }
    }
    for (int index = 0; index < amount; ++index)
    {
      ProcessStartInfo startInfo = new ProcessStartInfo();
      startInfo.FileName = fullPath;
      startInfo.Arguments = !flag ? "-fl" : "-kjsdhfkasdhfjashgc";
      workingDir = fullPath.Remove(fullPath.LastIndexOf("\\"), 9);
      startInfo.WorkingDirectory = workingDir;
      Process.Start(startInfo);
