      return;
    if (_lvItemList.Items.Count < itemList.Count)
    {
      try
      {
        foreach (string str in itemList)
        {
          bool flag = false;
          foreach (ListViewItem listViewItem in _lvItemList.Items)
          {
            if (str == listViewItem.Text)
            {
              flag = true;
              break;
            }
          }
          if (_lvItemList.Items.Count == 0)
            flag = false;
          if (!flag)
            _lvItemList.Items.Add(new ListViewItem()
            {
              Text = str
            });
        }
      }
      catch (Exception ex)
      {
      }
    }
    else if (_lvItemList.Items.Count > itemList.Count)
    {
      try
      {
        for (int index = _lvItemList.Items.Count - 1; index >= 0; --index)
        {
          bool flag = false;
          foreach (string str in itemList)
          {
            if (str == _lvItemList.Items[index].Text)
            {
              flag = true;
              break;
            }
          }
          if (!flag)
            _lvItemList.Items.RemoveAt(index);
        }
      }
      catch (Exception ex)
      {
      }
    }
    else
    {
      if (_lvItemList.Items.Count != itemList.Count)
        return;
      try
      {
        for (int index = 0; index < _lvItemList.Items.Count; ++index)
        {
          if (_lvItemList.Items[index].Text != itemList[index])
            _lvItemList.Items[index].Text = itemList[index];
