  private void lvAllAccounts_ItemCheck(object sender, ItemCheckEventArgs e)
  {
    if (this.inhibitAutoCheck)
      e.NewValue = e.CurrentValue;
    int index1 = e.Index;
    ListViewItem listViewItem = (sender as ListView).Items[index1];
    AutoAccount account = (AutoAccount) null;
    int index2 = SmartClass.SearchAutoAccount((int) listViewItem.Tag);
    try
    {
      if (index2 != -1)
        account = frmLogin.GAuto.AllAutoAccounts[index2];
    }
    catch (Exception ex)
    {
    }
    if (e.NewValue == CheckState.Checked && account != null)
    {
      if (!this.lvAllAccounts.Focused)
        return;
      F.CheckAI(account);
    }
    else
    {
      if (e.NewValue != CheckState.Unchecked || account == null)
        return;
      F.UncheckAI(account);
    }
  }

