  private void NVLayDiemHienTai(AutoAccount account)
  {
    if (account == null || account.Myself == null)
      return;
    frmMain.DisableNhiemVu(account);
    account.Myself.TargetX = 0.0f;
    account.Myself.TargetY = 0.0f;
    account.Myself.TargetMapID = -1;
    account.Myself.Status = AllEnums.CharStatuses.IDLE;
    account.Myself.SavedStatus = AllEnums.CharStatuses.IDLE;
    account.Myself.MinorStatus = AllEnums.CharStatuses.IDLE;
    account.Myself.MinorStatus2 = AllEnums.CharStatuses.IDLE;
    account.Myself.MoveAcrossMap = false;
    account.Myself.IsLongMove = false;
    account.Myself.MoveLongTrigger = false;
    account.Myself.SavedPosX = account.Myself.PosX;
    account.Myself.SavedPosY = account.Myself.PosY;
    account.Myself.SavedMapID = account.Myself.MapID;
    account.Myself.LongMoveX = (int) account.Myself.PosX;
    account.Myself.LongMoveY = (int) account.Myself.PosY;
    account.Myself.LongMoveMapID = account.Myself.MapID;
    account.Myself.UseAutoMove = false;
    account.Myself.MoveFlashPos = false;
    account.Myself.IsPK = false;
    account.CallMoveFlashPos((int) account.Myself.PosX, (int) account.Myself.PosY);
    if (account.Settings.AIMode != AllEnums.AIModes.DANHQUANHDIEM && account.Settings.AIMode != AllEnums.AIModes.HOTRO)
      account.Settings.AIMode = AllEnums.AIModes.DANHTUDO;
    this.GetCurrentPosMap(account);
    if (account.Myself.Level <= 20)
    {
      account.Settings.cboxTuNhatVatPham = false;
      account.Settings.cboxTuUpLevel = true;
    }
    if (this.lvAllAccounts.SelectedItems.Count > 0)
    {
      this.lvAllAccounts.SelectedItems[0].Checked = true;
    }
    else
    {
      ListViewItem listViewItem1 = (ListViewItem) null;
      try
      {
        foreach (ListViewItem listViewItem2 in this.lvAllAccounts.Items)
        {
          if ((int) listViewItem2.Tag == account.Target.ProcessID)
          {
            listViewItem1 = listViewItem2;
            break;
          }
        }
      }
      catch (Exception ex)
      {
      }
      if (listViewItem1 != null)
        listViewItem1.Checked = true;
    }
    account.IsAIEnabled = true;
  }

