      this.PopulateTabTienIch();
      this.PopulateGroupToDoi();
      this.PopulateGroupChat();
      this.PopulateGroupDied();
      this.PopulateGroupVeThanh();
    }
    else if (tabControl.SelectedTab.Name == frmLogin.GAuto.Settings.TabVatPhamName)
      this.PopulateTabVatPham();
    else if (tabControl.SelectedTab.Name == frmLogin.GAuto.Settings.tabThuongNhanName)
      this.PopulateTabThuongNhan();
    else if (tabControl.SelectedTab.Name == frmLogin.GAuto.Settings.tabNhiemVuName)
      this.PopulateTabNhiemVu();
    else if (tabControl.SelectedTab.Name == frmLogin.GAuto.Settings.tabDebugName || tabControl.SelectedTab.Name == "tabDebugBoc" || tabControl.SelectedTab.Name == "tabPhat")
      this.PopulateTabDebug();
    Stopwatch stopwatch1 = new Stopwatch();
    Stopwatch stopwatch2 = new Stopwatch();
    stopwatch2.Start();
    stopwatch1.Start();
    stopwatch1.Stop();
    stopwatch2.Stop();
  }

