  private static string ConvertToVBD(int totalValue)
  {
    int result1 = 0;
    int result2 = 0;
    int result3 = 0;
    string str = totalValue.ToString("000000");
    int.TryParse(str.Substring(0, 2), out result1);
    int.TryParse(str.Substring(2, 2), out result2);
    int.TryParse(str.Substring(4, 2), out result3);
    string vbd = $"({result1}v {result2}b {result3}d)";
    if (frmLogin.CompilingLanguage == "EN")
      vbd = $"({result1}g {result2}s {result3}c)";
    return vbd;
  }

