  public int SkillButtonComparer2(SkillPlayItem skillA, SkillPlayItem skillB)
  {
    return this.SkilButtonComparer($"{skillA.SkillItem.KeyDesc} - {skillA.SkillItem.Name}", $"{skillB.SkillItem.KeyDesc} - {skillB.SkillItem.Name}");
