  public int SkillButtonComparerName(string buttonA, string buttonB)
  {
    return string.Compare(frmMain.StripSkillName(buttonA), frmMain.StripSkillName(buttonB));
  }

  public int SkilButtonComparer(string buttonA, string buttonB)
  {
    char ch1 = buttonA[0];
    char ch2 = buttonB[0];
    if (buttonA.StartsWith("F"))
      buttonB.StartsWith("F");
    if (ch1 == 'F' && ch2 != 'F')
      return -1;
    if (ch1 != 'F' && ch2 == 'F')
      return 1;
    if ((ch1 != 'F' || ch2 != 'F') && ch1 != 'F' && (int) ch1 != (int) ch2)
    {
      if ((int) ch1 < (int) ch2)
        return -1;
      return (int) ch1 > (int) ch2 ? 1 : 0;
    }
    int num1 = buttonA.IndexOf('-');
    string s1 = "";
    if (num1 >= 0)
      s1 = buttonA.Substring(1, num1 - 2);
    string s2 = "";
    int num2 = buttonB.IndexOf('-');
    if (num2 >= 0)
      s2 = buttonB.Substring(1, num2 - 2);
    int result1 = 0;
    int result2 = 0;
    int.TryParse(s1, out result1);
    int.TryParse(s2, out result2);
    if (result1 < result2)
      return -1;
    return result1 > result2 ? 1 : 0;
  }

