  private static string StripSkillName(string inputName)
  {
    string str = inputName;
    if (inputName.Contains("-"))
    {
      int num = inputName.IndexOf('-');
      if (num > 0)
        str = inputName.Substring(num + 2, inputName.Length - num - 2);
    }
    return str;
  }

  private void EditSkillList(GAutoList<SkillPlayItem> skillList, ListView myLvSkills, int mode)
  {
    if (myLvSkills.SelectedItems.Count <= 0 || !(myLvSkills.SelectedItems[0].Text != "") || skillList.Count <= 0)
      return;
    string str = frmMain.StripSkillName(myLvSkills.SelectedItems[0].Text);
    try
    {
      for (int index = 0; index < skillList.Count; ++index)
      {
        SkillPlayItem skill = skillList[index];
        bool flag = false;
        if (skill.SkillItem.Name == str)
          flag = true;
        if (!flag && frmLogin.GAuto.CurrentAuto != null && frmLogin.GAuto.CurrentAuto.Myself.Menpai == AllEnums.Menpais.QUYCOC && GA.GetValidSkillName(skill.SkillItem.Name) == str)
          flag = true;
        if (flag)
        {
          string s = Interaction.InputBox(string.Format(frmMain.langChangeDelay, (object) skill.SkillItem.Name), frmMain.langChangeDelay2, skill.SkillDelayInSecond.ToString());
          int result = 0;
          int.TryParse(s, out result);
          if (result <= 0)
            break;
          skill.SkillDelayInSecond = result;
          myLvSkills.SelectedItems[0].SubItems[1].Text = skill.SkillDelayInSecond.ToString();
          if (mode == 0)
          {
            frmLogin.GAuto.CurrentAuto.SkillPlayList_OnAdd((object) skillList, (EventArgs) null);
            break;
          }
          if (mode == 1 || mode != 2)
            break;
          frmLogin.GAuto.CurrentAuto.SkillPKList_OnAdd((object) skillList, (EventArgs) null);
          break;
        }
      }
    }
    catch (Exception ex)
    {
    }
  }

  public void AddSkillToList(
    GAutoList<SkillPlayItem> skillList,
    string skillName,
    int skillDelay,
    int mode = 0,
    params object[] extraParams)
  {
    if (frmLogin.GAuto.CurrentAuto == null || frmLogin.GAuto.CurrentAuto.MySkills == null || !(skillName != "") || skillDelay < 0)
      return;
    skillName = frmMain.StripSkillName(skillName);
    bool flag1 = false;
    try
    {
      foreach (SkillPlayItem skill in (List<SkillPlayItem>) skillList)
      {
        bool flag2 = false;
        if (skill.SkillItem.Name == skillName)
          flag2 = true;
        if (!flag2 && frmLogin.GAuto.CurrentAuto.Myself.Menpai == AllEnums.Menpais.QUYCOC && skillName == GA.GetValidSkillName(skill.SkillItem.Name))
          flag2 = true;
        if (flag2)
        {
          switch (mode)
          {
            case 0:
              skill.SkillDelayInSecond = skillDelay;
              flag1 = true;
              goto label_17;
            case 1:
              if (extraParams.Length >= 5)
              {
                skill.SkillDelayInSecond = skillDelay;
                skill.WaitingTime = (int) extraParams[0];
                skill.BuffSelf = (bool) extraParams[1];
                skill.BuffParty = (bool) extraParams[2];
                skill.BuffList = (bool) extraParams[3];
                skill.BuffArmy = (bool) extraParams[4];
              }
              flag1 = true;
              goto label_17;
            default:
              continue;
          }
        }
      }
    }
    catch (Exception ex)
    {
    }
label_17:
    if (!flag1)
    {
      try
      {
        for (int index = 1; index < frmLogin.GAuto.CurrentAuto.MySkills.AllSkills.Count; ++index)
        {
          SingleSkill allSkill = frmLogin.GAuto.CurrentAuto.MySkills.AllSkills[index];
          if (allSkill.Name == skillName)
          {
            SkillPlayItem skillPlayItem = new SkillPlayItem()
            {
              SkillItem = new SingleSkill()
            };
            skillPlayItem.SkillItem.ID = allSkill.ID;
            skillPlayItem.SkillItem.BookSlot = allSkill.BookSlot;
            skillPlayItem.SkillItem.BuffPeriod = allSkill.BuffPeriod;
            skillPlayItem.SkillItem.DefaultDelay = allSkill.DefaultDelay;
            skillPlayItem.SkillItem.DelayIndex = allSkill.DelayIndex;
            skillPlayItem.SkillItem.KeyDesc = allSkill.KeyDesc;
            skillPlayItem.SkillItem.Name = frmLogin.GAuto.CurrentAuto.Myself.Menpai != AllEnums.Menpais.QUYCOC ? allSkill.Name : GA.GetValidSkillName(allSkill.Name);
            skillPlayItem.SkillItem.RageRequired = allSkill.RageRequired;
            skillPlayItem.SkillItem.SkillBook = allSkill.SkillBook;
            skillPlayItem.SkillItem.Special = allSkill.Special;
            skillPlayItem.Played = false;
            skillPlayItem.SkillDelayInSecond = skillDelay;
            skillPlayItem.IsEnabled = true;
            if (mode == 1 && extraParams.Length >= 5)
            {
              skillPlayItem.WaitingTime = (int) extraParams[0];
              skillPlayItem.BuffSelf = (bool) extraParams[1];
              skillPlayItem.BuffParty = (bool) extraParams[2];
              skillPlayItem.BuffList = (bool) extraParams[3];
              skillPlayItem.BuffArmy = (bool) extraParams[4];
            }
            skillList.Add(skillPlayItem);
            break;
          }
        }
      }
      catch (Exception ex)
      {
      }
    }
    skillList.Sort(new Comparison<SkillPlayItem>(this.SkillButtonComparer2));
    if (skillList != frmLogin.GAuto.CurrentAuto.Settings.SkillBuffList)
      return;
    frmLogin.GAuto.CurrentAuto.SkillBuffList_OnAdd((object) frmLogin.GAuto.CurrentAuto.Settings.SkillBuffList, (EventArgs) null);
  }

