  private static void LenBai(AutoAccount account)
  {
    if (account == null || account.Myself == null)
      return;
    if (account.Settings.AIMode != AllEnums.AIModes.DANHTUDO && account.Settings.AIMode != AllEnums.AIModes.HOTRO)
      account.Settings.AIMode = AllEnums.AIModes.DANHQUANHDIEM;
    if (account.Myself.SavedMapID >= 0 && (double) account.Myself.SavedPosX > 0.0 && (double) account.Myself.SavedPosY > 0.0 || account.Settings.CenterX > 0.0 && account.Settings.CenterY > 0.0 && account.Settings.MapID >= 0 && account.Settings.AIMode != AllEnums.AIModes.THUONGNHAN)
    {
      if (account.Myself.HorseType == -1)
        account.CallMounting();
      if (account.Myself.MapID == 0 || account.Myself.MapID == 1 || account.Myself.MapID == 2 || account.Myself.MapID == 186 || account.Myself.MapID == 480 || account.Myself.MapID == 181)
      {
        account.Myself.Status = AllEnums.CharStatuses.LENLAIBAITRAIN;
      }
      else
      {
        account.Settings.SavedPosX = (float) account.Settings.CenterX;
        account.Settings.SavedPosY = (float) account.Settings.CenterY;
        account.Settings.SavedMapID = account.Settings.MapID;
        account.Myself.SavedMapID = account.Settings.SavedMapID;
        account.Myself.SavedPosX = account.Settings.SavedPosX;
        account.Myself.SavedPosY = account.Settings.SavedPosY;
        account.Myself.LongMoveMapID = account.Settings.MapID;
        account.Myself.LongMoveX = (int) account.Settings.CenterX;
        account.Myself.LongMoveY = (int) account.Settings.CenterY;
        if (account.Target.VersionNum == 3 || account.Target.VersionNum == 4)
          account.Myself.IsLongMove = true;
        else
          account.Myself.MoveLongTrigger = true;
        account.Myself.SavedStatus = AllEnums.CharStatuses.IDLE;
        account.Myself.Status = AllEnums.CharStatuses.GOGOGO;
      }
      account.IsAIEnabled = true;
    }
    else if (account.Settings.AIMode == AllEnums.AIModes.THUONGNHAN)
    {
      int num1 = (int) MessageBox.Show(frmMain.langTNModeNoBTTS);
    }
    else
    {
      int num2 = (int) MessageBox.Show(frmMain.langPickAMap);
    }
  }

