  private static void HoiSucLenBai(AutoAccount account)
  {
    if (account == null || account.Myself == null)
      return;
    if (account.Settings.AIMode != AllEnums.AIModes.DANHTUDO && account.Settings.AIMode != AllEnums.AIModes.HOTRO)
      account.Settings.AIMode = AllEnums.AIModes.DANHQUANHDIEM;
    if (account.Myself.IsPartyFollowed == (byte) 1)
    {
      account.CallRemovePartyFollow();
      Thread.Sleep(500);
    }
    if (account.Myself.HorseType == -1)
      account.CallMounting();
    account.Settings.SavedPosX = (float) account.Settings.CenterX;
    account.Settings.SavedPosY = (float) account.Settings.CenterY;
    account.Settings.SavedMapID = account.Settings.MapID;
    account.Myself.Status = AllEnums.CharStatuses.VETHANH;
    account.Myself.SavedStatus = AllEnums.CharStatuses.VETHANH;
    account.IsAIEnabled = true;
    account.Settings.TraderMode = AllEnums.TraderModes.IDLE;
    if (!account.Settings.cboxTheoSau)
      return;
    account.Myself.actionFlag = 1;
    account.Settings.cboxTheoSau = false;
  }

