  private void cboxBuocTheoSat_CheckedChanged(object sender, EventArgs e)
  {
    CheckBox checkBox = sender as CheckBox;
    if (frmLogin.GAuto.CurrentAuto == null || !checkBox.Focused)
      return;
    if (!checkBox.Checked)
      return;
    try
    {
      if (frmLogin.GAuto.CurrentAuto.MyParty.PartyNumbers <= 0 || frmLogin.GAuto.AllAutoAccounts.Count <= 1)
        return;
      for (int index1 = frmLogin.GAuto.AllAutoAccounts.Count - 1; index1 >= 0; --index1)
      {
        if (frmLogin.GAuto.AllAutoAccounts[index1].Myself.ID != frmLogin.GAuto.CurrentAuto.Myself.ID && !frmLogin.GAuto.AllAutoAccounts[index1].Settings.cboxTheoSau)
        {
          for (int index2 = 0; index2 <= frmLogin.GAuto.CurrentAuto.MyParty.PartyNumbers; ++index2)
          {
            if (frmLogin.GAuto.CurrentAuto.MyParty.AllMembers[index2].Name == frmLogin.GAuto.AllAutoAccounts[index1].Myself.Name)
            {
              frmLogin.GAuto.AllAutoAccounts[index1].Settings.cboxTheoSau = true;
              frmLogin.GAuto.AllAutoAccounts[index1].Settings.AIMode = AllEnums.AIModes.DANHTUDO;
              break;
            }
          }
        }
      }
    }
    catch (Exception ex)
    {
    }
  }

