  private void GetTTNPC(int npcTime)
  {
    if (frmLogin.GAuto.CurrentAuto == null)
      return;
    AutoAccount currentAuto = frmLogin.GAuto.CurrentAuto;
    if (frmLogin.GAuto.CurrentAuto.Myself.HasTarget)
    {
      int targetId = frmLogin.GAuto.CurrentAuto.MyQuai.TargetID;
      if (targetId < 0)
      {
        int num = (int) MessageBox.Show(string.Format(frmMain.langPickNPC1, (object) npcTime.ToString()), frmMain.langPickTarget, MessageBoxButtons.OK, MessageBoxIcon.Hand);
      }
      else
      {
        QuaiIndividual quaiIndividual1 = (QuaiIndividual) null;
        try
        {
          for (int index = currentAuto.MyQuai.AllQuai.Count - 1; index >= 0; --index)
          {
            QuaiIndividual quaiIndividual2 = frmLogin.GAuto.CurrentAuto.MyQuai.AllQuai[index];
            if (quaiIndividual2.ID == targetId)
            {
              quaiIndividual1 = quaiIndividual2;
              break;
            }
          }
        }
        catch (Exception ex)
        {
        }
        if (quaiIndividual1 == null)
          return;
        if (npcTime == 1)
        {
          this.txtTTNPC1_ID.Text = quaiIndividual1.ID.ToString("0");
          currentAuto.Settings.txtTTNPC1_ID = quaiIndividual1.ID;
          this.txtTTNPC1_X.Text = quaiIndividual1.PosX.ToString("0");
          currentAuto.Settings.txtTTNPC1_X = (int) quaiIndividual1.PosX;
          this.txtTTNPC1_Y.Text = quaiIndividual1.PosY.ToString("0");
          currentAuto.Settings.txtTTNPC1_Y = (int) quaiIndividual1.PosY;
        }
        else
        {
          if (npcTime != 2)
            return;
          this.txtTTNPC2_ID.Text = quaiIndividual1.ID.ToString("0");
          currentAuto.Settings.txtTTNPC2_ID = quaiIndividual1.ID;
          this.txtTTNPC2_X.Text = quaiIndividual1.PosX.ToString("0");
          currentAuto.Settings.txtTTNPC2_X = (int) quaiIndividual1.PosX;
          this.txtTTNPC2_Y.Text = quaiIndividual1.PosY.ToString("0");
          currentAuto.Settings.txtTTNPC2_Y = (int) quaiIndividual1.PosY;
        }
      }
    }
    else
    {
      int num1 = (int) MessageBox.Show(string.Format(frmMain.langPickNPC2, (object) npcTime.ToString()), frmMain.langPickTarget, MessageBoxButtons.OK, MessageBoxIcon.Hand);
    }
  }

