  public void NVAcTac(AutoAccount account)
  {
    if (account == null || !frmLogin.GAuto.Settings.IsPro1 || !this.cboxIsAcTac.Focused && !account.Myself.hotkeyIsAcTac && !account.MyFlag.DatLichFlag)
      return;
    account.MyFlag.DatLichFlag = false;
    if (!account.IsPartyKey() && account.MyParty.PartyNumbers > 2)
    {
      GA.ShowBalloon(frmMain.langMustBePTKey, frmMain.langNotPTKey, account, 10000);
      account.Myself.IsAcTac = false;
    }
    else
    {
      account.Myself.IsAcTac = !account.Myself.IsAcTac;
      account.Myself.hotkeyIsAcTac = false;
      this.lvAllAccounts.Focus();
      if (account.Myself.IsAcTac)
      {
        if (account.Target.SubVersion == 4 && frmLogin.GAuto.Settings.EnableAT == 0)
        {
          account.Myself.IsAcTac = false;
          this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + account.Myself.Name, frmMain.langDisableTinhNang, ToolTipIcon.Info);
        }
        else
        {
          account.Myself.IsAcTac = frmMain.DisableNhiemVu(account);
          account.IsAIEnabled = true;
          account.Myself.Status = AllEnums.CharStatuses.IDLE;
          account.Settings.AIMode = AllEnums.AIModes.NHIEMVU;
          account.Myself.MoveAcrossMap = false;
          account.Myself.MoveLongTrigger = false;
          account.Myself.IsLongMove = false;
          account.MyFlag.phubanActionCounter = 0;
          if (account.Myself.CharType == AllEnums.CharTypes.NUKER)
            account.Settings.FightMode = AllEnums.FightingModes.DANHGOMQUAI;
          frmMain.ResetPhoBan(account);
          this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + account.Myself.Name, frmMain.langATStart, ToolTipIcon.Info);
        }
      }
      else
      {
        account.CallMoveFlashPos((int) account.Myself.PosX, (int) account.Myself.PosY);
        account.Myself.Status = AllEnums.CharStatuses.IDLE;
        account.IsAIEnabled = false;
        this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + account.Myself.Name, frmMain.langATStop, ToolTipIcon.Info);
      }
    }
  }

