  private void lvAllAccounts_MouseDown(object sender, MouseEventArgs e)
  {
    this.inhibitAutoCheck = true;
    this._itemDnD = this.lvAllAccounts.GetItemAt(e.X, e.Y);
  }

  private void lvAllAccounts_MouseUp(object sender, MouseEventArgs e)
  {
    this.inhibitAutoCheck = false;
    if (this._itemDnD == null)
      return;
    try
    {
      ListViewItem itemAt = this.lvAllAccounts.GetItemAt(0, Math.Min(e.Y, this.lvAllAccounts.Items[this.lvAllAccounts.Items.Count - 1].GetBounds(ItemBoundsPortion.Entire).Bottom - 1));
      if (itemAt == null)
        return;
      Rectangle bounds = itemAt.GetBounds(ItemBoundsPortion.Entire);
      bool flag = e.Y < bounds.Top + bounds.Height / 2;
      if (this._itemDnD != itemAt)
      {
        if (flag)
        {
          this.lvAllAccounts.Items.Remove(this._itemDnD);
          this.lvAllAccounts.Items.Insert(itemAt.Index, this._itemDnD);
        }
        else
        {
          this.lvAllAccounts.Items.Remove(this._itemDnD);
          this.lvAllAccounts.Items.Insert(itemAt.Index + 1, this._itemDnD);
        }
      }
      this.lvAllAccounts.LineAfter = this.lvAllAccounts.LineBefore = -1;
      this.lvAllAccounts.Invalidate();
    }
    finally
    {
      this._itemDnD = (ListViewItem) null;
      this.Cursor = Cursors.Default;
    }
  }

