  private void NVTrungAc(AutoAccount account)
  {
    if (account == null || !frmLogin.GAuto.Settings.IsPro1 || !this.cboxIsTrungAc.Focused && !account.Myself.hotkeyIsTrungAc && !account.MyFlag.DatLichFlag)
      return;
    account.MyFlag.DatLichFlag = false;
    account.Myself.IsTrungAc = !account.Myself.IsTrungAc;
    account.Myself.hotkeyIsTrungAc = false;
    this.lvAllAccounts.Focus();
    if (account.Target.VersionNum == 4)
    {
      int num1 = 0;
      int num2 = 2;
      if (!GA.isVipMember())
      {
        try
        {
          for (int index = frmLogin.GAuto.AllAutoAccounts.Count - 1; index >= 0; --index)
          {
            if (frmLogin.GAuto.AllAutoAccounts[index].Myself.IsTrungAc && frmLogin.GAuto.AllAutoAccounts[index].Myself.DatabaseID != account.Myself.DatabaseID)
            {
              ++num1;
              if (num1 >= num2)
              {
                account.IsAIEnabled = false;
                account.Myself.IsTrungAc = false;
                this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + account.Myself.Name, "You can only auto Fight Evil from 2 character", ToolTipIcon.Info);
                return;
              }
            }
          }
        }
        catch (Exception ex)
        {
        }
      }
    }
    if (account.Myself.IsTrungAc)
    {
      account.Myself.IsTrungAc = frmMain.DisableNhiemVu(account);
      account.IsAIEnabled = true;
      account.Settings.cboxTANhanNV = true;
      account.Settings.cboxTuNhatVatPham = true;
      account.Settings.cboxDanhQuai = true;
      account.Myself.Status = AllEnums.CharStatuses.IDLE;
      account.Settings.AIMode = AllEnums.AIModes.NHIEMVU;
      account.Myself.MoveAcrossMap = false;
      account.Myself.MoveLongTrigger = false;
      account.Myself.IsLongMove = false;
      account.Myself.TrungAcFinished = false;
      account.Myself.TrungAcFoundQuai = false;
      account.Myself.TrungAcItemUsed = false;
      account.Myself.TrungAcQuaiDied = false;
      account.Myself.TrungAcCheckMap = false;
      account.Myself.TrungAcMoveStamp = 0L;
      account.Myself.TrungAcFoundBoc = false;
      account.Myself.TrungAcQuaiList.Clear();
      account.ResetPacketMsg();
      account.ResetEventString();
      account.Settings.FightMode = AllEnums.FightingModes.DANHTUNGCON;
      if (account.Settings.cboxDanhTheoKey && account.MyParty.PartyNumbers > 0)
        account.Settings.cboxDanhTheoKey = false;
      if (account.Settings.txtDanhTheoAi != "" && account.Settings.cboxDanhTheoAi)
        account.Settings.cboxDanhTheoAi = false;
      this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + account.Myself.Name, frmMain.langTAStart, ToolTipIcon.Info);
    }
    else
    {
      account.CallMoveFlashPos((int) account.Myself.PosX, (int) account.Myself.PosY);
      account.Myself.Status = AllEnums.CharStatuses.IDLE;
      account.IsAIEnabled = false;
      this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + account.Myself.Name, frmMain.langTAStop, ToolTipIcon.Info);
    }
  }

