  internal static void BindPetListToListView(
    AutoAccount account,
    ListView _lvItemList,
    GAutoList<string> existList)
  {
    List<PlayerListItem> playerListItemList = new List<PlayerListItem>();
    if (account.MyPet != null)
    {
      if (account.MyQuai.AllQuai.Count > 0)
      {
        try
        {
          for (int index = account.MyQuai.AllQuai.Count - 1; index >= 0; --index)
          {
            QuaiIndividual quaiIndividual = account.MyQuai.AllQuai[index];
            if (quaiIndividual.CanAttack == byte.MaxValue)
            {
              bool flag = false;
              if (existList != null && existList.Count > 0)
              {
                foreach (string exist in (List<string>) existList)
                {
                  if (exist == quaiIndividual.Name)
                  {
                    flag = true;
                    break;
                  }
                }
              }
              if (!flag)
                playerListItemList.Add(new PlayerListItem()
                {
                  Name = quaiIndividual.Name,
                  Level = quaiIndividual.Level
                });
            }
          }
        }
        catch (Exception ex)
        {
        }
      }
    }
    if (playerListItemList == null)
      return;
    if (_lvItemList.Items.Count < playerListItemList.Count)
    {
      try
      {
        foreach (PlayerListItem playerListItem in playerListItemList)
        {
          bool flag = false;
          foreach (ListViewItem listViewItem in _lvItemList.Items)
          {
            if (playerListItem.Name == listViewItem.Text)
            {
              flag = true;
              break;
            }
          }
          if (_lvItemList.Items.Count == 0)
            flag = false;
          if (!flag)
            _lvItemList.Items.Add(new ListViewItem()
            {
              Text = playerListItem.Name,
              SubItems = {
                playerListItem.Level.ToString()
              }
            });
        }
      }
      catch (Exception ex)
      {
      }
    }
    else if (_lvItemList.Items.Count > playerListItemList.Count)
    {
      try
      {
        for (int index = _lvItemList.Items.Count - 1; index >= 0; --index)
        {
          bool flag = false;
          foreach (PlayerListItem playerListItem in playerListItemList)
          {
            if (playerListItem.Name == _lvItemList.Items[index].Text)
            {
              flag = true;
              break;
            }
          }
          if (!flag)
            _lvItemList.Items.RemoveAt(index);
        }
      }
      catch (Exception ex)
      {
      }
    }
    else
    {
      if (_lvItemList.Items.Count != playerListItemList.Count)
        return;
      try
      {
        for (int index = 0; index < _lvItemList.Items.Count; ++index)
        {
          if (_lvItemList.Items[index].Text != playerListItemList[index].Name)
            _lvItemList.Items[index].Text = playerListItemList[index].Name;
        }
      }
      catch (Exception ex)
      {
      }
    }
  }

