  public static void ReadMyValueString(AutoAccount account, ref string myref, int mybase)
  {
    byte[] numArray = new byte[60];
    int lpNumberOfBytesRead = 0;
    Array.Clear((Array) numArray, 0, numArray.Length);
    MyDLL.ReadProcessMemory((int) account.Target.ProcessHandle, (IntPtr) mybase, numArray, (uint) numArray.Length, ref lpNumberOfBytesRead);
    bool flag = false;
    int index = 0;
    while (!flag)
    {
      if (numArray[index] == (byte) 0 || index >= numArray.Length - 1)
        flag = true;
      ++index;
    }
    Encoding.UTF7.GetString(numArray, 0, index - 1);
    if (account.Target.VersionNum == 7 || account.Target.VersionNum == 8 || account.Target.VersionNum == 5 || account.Target.VersionNum == 6)
    {
      string str = Encoding.GetEncoding("gb2312").GetString(numArray, 0, index - 1);
      myref = str;
    }
    else
      myref = GA.ConvertToUnicode(numArray, 0, index - 1);
  }

