  private void lvAllAccounts_MouseMove(object sender, MouseEventArgs e)
  {
    if (this._itemDnD == null)
      return;
    this.Cursor = Cursors.Hand;
    ListViewItem itemAt = this.lvAllAccounts.GetItemAt(0, Math.Min(e.Y, this.lvAllAccounts.Items[this.lvAllAccounts.Items.Count - 1].GetBounds(ItemBoundsPortion.Entire).Bottom - 1));
    if (itemAt == null)
      return;
    Rectangle bounds = itemAt.GetBounds(ItemBoundsPortion.Entire);
    if (e.Y < bounds.Top + bounds.Height / 2)
    {
      this.lvAllAccounts.LineBefore = itemAt.Index;
      this.lvAllAccounts.LineAfter = -1;
    }
    else
    {
      this.lvAllAccounts.LineBefore = -1;
      this.lvAllAccounts.LineAfter = itemAt.Index;
    }
    this.lvAllAccounts.Invalidate();
  }

