  private void btnPhuBanOpen_Click(object sender, EventArgs e)
  {
    OpenFileDialog openFileDialog = new OpenFileDialog();
    openFileDialog.Filter = "GAuto Pathpoints Files | *.txt";
    DialogResult dialogResult = openFileDialog.ShowDialog();
    string str = "";
    if (dialogResult == DialogResult.OK)
      str = openFileDialog.FileName;
    if (string.IsNullOrEmpty(str))
      return;
    frmLogin.GAuto.Settings.PathPointFile = str;
    this.RefreshPathPoints();
  }

  private void RefreshPathPoints()
  {
    if (!System.IO.File.Exists(frmLogin.GAuto.Settings.PathPointFile))
      return;
    try
    {
      StreamReader streamReader = new StreamReader(frmLogin.GAuto.Settings.PathPointFile);
      if (streamReader == null)
        return;
      int num = 1;
      frmLogin.UserPathPoints.Clear();
      while (!streamReader.EndOfStream)
      {
        string input = streamReader.ReadLine();
        if (!input.StartsWith("#") && !string.IsNullOrEmpty(input))
        {
          if (input.StartsWith("["))
          {
            string midString = GA.GetMidString(input, "[", "]");
            UserPathPoint userPathPoint = new UserPathPoint();
            userPathPoint.SectionName = midString;
            num = 1;
            frmLogin.UserPathPoints.Add(userPathPoint);
          }
          else if (frmLogin.UserPathPoints.Count > 0)
          {
            string[] strArray = input.Split(',');
            if (strArray.Length >= 4)
            {
              InMapPoint inMapPoint = new InMapPoint();
              int.TryParse(strArray[0], out inMapPoint.PosX);
              int.TryParse(strArray[1], out inMapPoint.PosY);
              int.TryParse(strArray[2], out inMapPoint.Horse);
              int.TryParse(strArray[3], out inMapPoint.PartyFollow);
              inMapPoint.STT = num;
              ++num;
              frmLogin.UserPathPoints[frmLogin.UserPathPoints.Count - 1].PathPoints.Add(inMapPoint);
            }
          }
        }
      }
      streamReader.Close();
      try
      {
        frmMain.frmMainInstance.cboPhuBanPP.Invoke((Delegate) (() =>
        {
          this.cboPhuBanPP.BeginUpdate();
          this.cboPhuBanPP.Items.Clear();
          this.cboPhuBanPP.Items.Add((object) frmMain.langNotYet);
          for (int index = 0; index < frmLogin.UserPathPoints.Count; ++index)
            this.cboPhuBanPP.Items.Add((object) frmLogin.UserPathPoints[index].SectionName);
          this.cboPhuBanPP.EndUpdate();
        }));
      }
      catch (Exception ex)
      {
      }
    }
    catch (Exception ex)
    {
      GA.WriteUserLog(frmMain.langErrorPathPoint + ex.Message);
    }
  }

