  private static void EnableSkillLVItem(
    ItemCheckEventArgs e,
    ListView listview,
    GAutoList<SkillPlayItem> skillist)
  {
    if (!listview.Focused)
      return;
    int index1 = e.Index;
    ListViewItem listViewItem = listview.Items[index1];
    AutoAccount currentAuto = frmLogin.GAuto.CurrentAuto;
    for (int index2 = 0; index2 < skillist.Count; ++index2)
    {
      if (skillist[index2].SkillItem.Name == frmMain.StripSkillName(listview.Items[index1].Text))
      {
        if (e.NewValue == CheckState.Checked)
          skillist[index2].IsEnabled = true;
        else
          skillist[index2].IsEnabled = false;
        if (skillist == currentAuto.Settings.SkillPlayList)
        {
          currentAuto.SkillPlayList_OnAdd((object) skillist, (EventArgs) null);
          break;
        }
        if (skillist == currentAuto.Settings.SkillPKList)
        {
          currentAuto.SkillPKList_OnAdd((object) skillist, (EventArgs) null);
          break;
        }
        if (skillist != currentAuto.Settings.SkillBuffList)
          break;
        currentAuto.SkillBuffList_OnAdd((object) skillist, (EventArgs) null);
        break;
      }
    }
  }

