  }

  private void ResetSelectAccounts()
  {
    if (this.lvAllAccounts.SelectedItems.Count <= 0)
      return;
    try
    {
      List<int> indexRemove = new List<int>();
      foreach (ListViewItem selectedItem in this.lvAllAccounts.SelectedItems)
      {
        for (int index = frmLogin.GAuto.AllAutoAccounts.Count - 1; index >= 0; --index)
        {
          if (frmLogin.GAuto.AllAutoAccounts[index].Target.ProcessID == (int) selectedItem.Tag)
            indexRemove.Add(index);
        }
      }
      if (indexRemove.Count <= 0)
        return;
      indexRemove.Sort();
      new Thread((ThreadStart) (() =>
      {
        for (int index = indexRemove.Count - 1; index >= 0; --index)
          SmartClass.RemoveAutoAccount(indexRemove[index], isToReset: true);
      })).Start();
    }
    catch (Exception ex)
    {
    }
  }

