  public void cboxCheDo_CheckedChanged(object sender, EventArgs e)
  {
    if (frmLogin.GAuto.CurrentAuto == null)
      return;
    AutoAccount currentAuto = frmLogin.GAuto.CurrentAuto;
    if (!this.cboxIsCheDo.Focused)
      return;
    currentAuto.Myself.IsCheDo = !currentAuto.Myself.IsCheDo;
    this.lvAllAccounts.Focus();
    if (currentAuto.Myself.IsCheDo)
    {
      if ((currentAuto.Target.SubVersion == 4 || currentAuto.Target.SubVersion == 17) && frmLogin.GAuto.Settings.EnableCheDo == 0 && !GA.isVipMember() && !GA.IsCTVMember())
      {
        currentAuto.Myself.IsCheDo = false;
        this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + currentAuto.Myself.Name, frmMain.langDisableTinhNang, ToolTipIcon.Info);
      }
      else
      {
        bool flag = true;
        int index = (frmLogin.GAuto.Settings.CheDoCounts ^ 2013) / 152;
        string str = "tnchedo";
        int currentIsTn = frmMain.GetCurrentIsTN(currentAuto, "tnchedo");
        if (frmLogin.GAuto.AllAutoAccounts.Count >= 1)
        {
          try
          {
            index = frmLogin.GAuto.Settings.Account.MyLicense.CountUnit(str);
            if (currentIsTn >= index)
              flag = false;
            if (!flag)
            {
              if (!frmLogin.GAuto.Settings.ThangCheDo)
              {
                int num = frmLogin.GAuto.Settings.Account.CraftingPrice;
                if (frmLogin.CompilingLanguage == "VN")
                  num = index + 1 >= frmLogin.GAuto.Settings.CheDoPrices.Count ? (int) frmLogin.GAuto.Settings.CheDoPrices[frmLogin.GAuto.Settings.CheDoPrices.Count - 1] : (int) frmLogin.GAuto.Settings.CheDoPrices[index];
                else if (frmLogin.CompilingLanguage != "VN")
                  num = (index + 1) * num;
                int currentDuration = (frmLogin.GAuto.Settings.CheDoDuration ^ 2013) / 152;
                bool fulltime = false;
                DateTime dateTime = DateTime.Now.Add(TimeSpan.FromSeconds((double) frmMain.GetSessionExpire(currentDuration, out fulltime)));
                this.ExtendTinhnang(1, 2, str, false, false);
                if (frmLogin.GAuto.Settings.Account.MyLicense.CountUnit(str) > currentIsTn)
                  flag = true;
                if (!flag)
                {
                  flag = false;
                  frmLogin.GAuto.Settings.CheDoStatus = 0;
                  if (frmLogin.GAuto.Settings.CheDoStatus == 0)
                    this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + currentAuto.Myself.Name, frmMain.langCheDoNoGG, ToolTipIcon.Info);
                }
                else
                {
                  GA.WriteUserLog(frmMain.langCheDoAdded, currentAuto, (object) (index + 1), (object) num, (object) dateTime.ToString());
                  flag = true;
                }
              }
              else if (frmLogin.GAuto.Settings.ThangCheDo)
              {
                int num1 = (int) MessageBox.Show("Để chế đồ, bạn phải bấm mua slot chế đồ 2, 5, hoặc 50 từ auto", "Chương trình Tháng Chế Đồ", MessageBoxButtons.OK, MessageBoxIcon.Asterisk);
              }
            }
          }
          catch (Exception ex)
          {
            if (!GA.isVipMember())
            {
              flag = false;
              GA.WriteUserLog(frmMain.langCheDoError, currentAuto);
            }
          }
        }
        if (flag)
        {
          if (currentAuto.Settings.cboItemCheDo == "Đao Phủ" || currentAuto.Settings.cboItemCheDo == "Thương Bổng" || currentAuto.Settings.cboItemCheDo == "Đơn Đoản" || currentAuto.Settings.cboItemCheDo == "Song Đoản" || currentAuto.Settings.cboItemCheDo == "Phiến" || currentAuto.Settings.cboItemCheDo == "Hoàn")
          {
            if (currentAuto.MySkills.AllSkills[3].ID != 46)
            {
              this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + currentAuto.Myself.Name, frmMain.langTinhLuyen1, ToolTipIcon.Info);
              currentAuto.Myself.IsCheDo = false;
              return;
            }
            GA.ShowBalloon(frmMain.langCheDoWarning1, frmMain.langCheDoWarning2, currentAuto);
          }
          else if (currentAuto.MySkills.AllSkills[1].ID != 48 /*0x30*/ || currentAuto.MySkills.AllSkills[2].ID != 47)
          {
            this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + currentAuto.Myself.Name, frmMain.langCheDoWarning3, ToolTipIcon.Info);
            currentAuto.Myself.IsCheDo = false;
            return;
          }
          currentAuto.Myself.IsCheDo = frmMain.DisableNhiemVu(currentAuto);
          currentAuto.IsAIEnabled = true;
          currentAuto.Myself.Status = AllEnums.CharStatuses.IDLE;
          currentAuto.Settings.AIMode = AllEnums.AIModes.NHIEMVU;
          currentAuto.Myself.MoveAcrossMap = false;
          currentAuto.Myself.MoveLongTrigger = false;
          currentAuto.Myself.IsLongMove = false;
          currentAuto.CallSoiItemOnOff(1);
          currentAuto.Myself.itemXinArrayGlobal.Clear();
          currentAuto.Myself.actionCounter = 0;
          currentAuto.Myself.NhiemVuFinish = false;
          currentAuto.ResetEventString();
          currentAuto.ResetPacketMsg();
          currentAuto.Myself.QuestStep = 0;
          currentAuto.Myself.actionFlag = 0;
          GA.ShowBalloon(frmMain.langCheDoStart, frmMain.langCharacter + currentAuto.Myself.Name, currentAuto);
        }
        else
        {
          this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + currentAuto.Myself.Name, frmMain.langCheDoNoGG2 + index.ToString() + frmMain.langCheDoNoGG3, ToolTipIcon.Info);
          currentAuto.Myself.IsCheDo = false;
        }
      }
    }
    else
    {
      currentAuto.CallMoveFlashPos((int) currentAuto.Myself.PosX, (int) currentAuto.Myself.PosY);
      currentAuto.Myself.Status = AllEnums.CharStatuses.IDLE;
      currentAuto.IsAIEnabled = false;
      this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + currentAuto.Myself.Name, frmMain.langCheDoStop, ToolTipIcon.Info);
    }
  }

