      return;
    account.MyFlag.HotkeyFlag = false;
    account.Myself.IsQ1 = !account.Myself.IsQ1;
    int num1 = account.Myself.IsQ1 ? 1 : 0;
    this.lvAllAccounts.Focus();
    if (GA.CheckGameVersion(account.Target.VersionNum) == 356)
    {
      this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + account.Myself.Name, frmMain.langDisableTinhNang, ToolTipIcon.Info);
      account.Myself.IsQ1 = false;
    }
    else if (account.Myself.IsQ1)
    {
      string str = "tnq12";
      bool flag = true;
      int num2 = 0;
      int num3 = 0;
      int num4 = 0;
      int num5 = num2;
      int num6 = frmLogin.GAuto.Settings.Account.MyLicense.CountUnit(str);
      int currentIsTn = frmMain.GetCurrentIsTN(account, str);
      if (currentIsTn >= num6)
        flag = false;
      if (!GA.isVipMember())
      {
        try
        {
          for (int index = frmLogin.GAuto.AllAutoAccounts.Count - 1; index >= 0; --index)
          {
            if ((frmLogin.GAuto.AllAutoAccounts[index].Myself.IsQ1 || frmLogin.GAuto.AllAutoAccounts[index].Myself.IsQ2) && frmLogin.GAuto.AllAutoAccounts[index].Myself.DatabaseID != account.Myself.DatabaseID)
            {
              ++num4;
              if (num4 >= num5)
              {
                flag = false;
                break;
              }
            }
          }
        }
        catch (Exception ex)
        {
        }
      }
      if (!flag)
      {
        if (frmLogin.CompilingLanguage == "EN" && num6 == 1)
        {
          account.Myself.IsQ1 = false;
          account.MyFlag.DatLichFlag = false;
          this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + account.Myself.Name, "You can only auto Q12 from 2 character", ToolTipIcon.Info);
          return;
        }
        account.MyFlag.waitMsgBox = true;
        int price = 2;
        if (frmLogin.CompilingLanguage != "VN")
          price = frmLogin.GAuto.Settings.Account.Price1HQuest;
        int currentDuration = (frmLogin.GAuto.Settings.Q12TCDuration ^ 1786) / 849;
        bool fulltime = false;
        TimeSpan tempSpan = TimeSpan.FromSeconds((double) frmMain.GetSessionExpire(currentDuration, out fulltime, "q1tc", buyPTOnly: true));
        DateTime.Now.Add(tempSpan);
        frmMain.CalcQuestPrice(ref price, ref tempSpan);
        this.ExtendTinhnang(1, 0, str, false, account.MyFlag.DatLichFlag);
        num3 = frmLogin.GAuto.Settings.Account.MyLicense.CountUnit(str);
        if (num3 > currentIsTn)
          flag = true;
        account.MyFlag.waitMsgBox = false;
      }
      account.MyFlag.DatLichFlag = false;
      if (!flag)
      {
        account.Myself.IsQ1 = false;
        this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + account.Myself.Name, frmMain.langQ12NoGG, ToolTipIcon.Info);
      }
      else
      {
        if (!flag)
          return;
        int num7 = 0;
        int num8 = num3 != 0 ? num3 : num6;
        account.MyFlag.Q1UuTien = false;
        for (int index = frmLogin.GAuto.AllAutoAccounts.Count - 1; index >= 0; --index)
        {
          AutoAccount allAutoAccount = frmLogin.GAuto.AllAutoAccounts[index];
          if (allAutoAccount.MyFlag.Q1UuTien)
          {
            if (!allAutoAccount.Myself.IsQ1)
              allAutoAccount.MyFlag.Q1UuTien = false;
            else
              ++num7;
            if (num7 > num8)
              allAutoAccount.MyFlag.Q1UuTien = false;
          }
        }
        if (num7 < num8)
          account.MyFlag.Q1UuTien = true;
        account.Myself.IsQ1 = frmMain.DisableNhiemVu(account);
        account.IsAIEnabled = true;
        account.Myself.trieutapCounter = 0;
        account.Myself.talkCounter = 0;
        account.Myself.actionCounter = 0;
        account.Myself.actionFlag = 0;
        account.Myself.InMapPathPoints.Clear();
        account.Myself.InPathPointIndex = 0;
        account.Myself.indexPPcalc = true;
        account.MyFlag.xongQstamp = 0L;
        account.Myself.NhiemVuFinish = false;
        account.MyFlag.fullQCounter = 0;
        account.MyFlag.nhanQstamp = 0L;
        account.MyFlag.xonglinhQ1 = false;
        account.Myself.PBxong1vong = false;
        account.MyFlag.sotquaiQ1 = false;
        account.MyQuai.noAttackArray.Clear();
        GA.PartyNoAttack(account);
        account.Myself.Status = AllEnums.CharStatuses.IDLE;
        account.Settings.AIMode = AllEnums.AIModes.NHIEMVU;
        account.Myself.MoveAcrossMap = false;
        account.Myself.MoveLongTrigger = false;
        account.Myself.IsLongMove = false;
        this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + account.Myself.Name, frmMain.langQ12Start, ToolTipIcon.Info);
      }
    }
    else
    {
      account.CallMoveFlashPos((int) account.Myself.PosX, (int) account.Myself.PosY);
      account.Myself.Status = AllEnums.CharStatuses.IDLE;
      account.IsAIEnabled = false;
      this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + account.Myself.Name, frmMain.langQ1Stop, ToolTipIcon.Info);
    }
  }

  private static void CalcQuestPrice(ref int price, ref TimeSpan tempSpan, string quest = "Q12")
  {
    double totalSeconds = tempSpan.TotalSeconds;
    double a1 = Math.Floor(totalSeconds / 10800.0);
    double a2 = (totalSeconds - a1 * 10800.0) / 3600.0;
    double num = (2000.0 * Math.Ceiling(a2) + 5000.0 * Math.Round(a1)) / 1000.0;
    if (num < 2.0)
      num = 2.0;
    if (frmLogin.CompilingLanguage != "VN")
      num = (double) frmLogin.GAuto.Settings.Account.Price1HQuest * Math.Ceiling(a2) + (double) frmLogin.GAuto.Settings.Account.Price3HQuest * Math.Round(a1);
    else if (quest == "Q12" && (frmLogin.GAuto.Settings.Q12_1hPrice == 0 || frmLogin.GAuto.Settings.Q12_3hPrice == 0))
    {
      price = 0;
      return;
    }
    price = (int) num;
  }

  public static int GetSessionExpire(
    int currentDuration,
    out bool fulltime,
    string tnkey = "",
    int packageMS = 3600,
    bool buyPTOnly = false)
  {
    fulltime = true;
    if (currentDuration <= 0)
    {
      if (frmLogin.GAuto.Settings.Account.IsLoginGGold)
      {
        int val2 = (int) frmLogin.GAuto.Settings.Account.RemainMSeconds / 1000;
        if (tnkey == string.Empty)
        {
          currentDuration = val2;
        }
        else
        {
          currentDuration = Math.Min(packageMS, val2);
          if (val2 < packageMS)
            fulltime = false;
        }
      }
      else if (!frmLogin.GAuto.Settings.Account.IsLoginGGold)
      {
        if (tnkey == string.Empty)
          currentDuration = 86400;
        else if (tnkey == "q1tc" || tnkey == "q2tc" || tnkey == "yto")
          currentDuration = packageMS;
      }
      return currentDuration;
    }
    if ((tnkey == "q1tc" || tnkey == "yto") && !buyPTOnly)
    {
      if (frmLogin.GAuto.Settings.Account.IsLoginGGold)
      {
        int num = (int) frmLogin.GAuto.Settings.Account.RemainMSeconds / 1000;
        if (currentDuration + packageMS <= num)
        {
          currentDuration += packageMS;
        }
        else
        {
          currentDuration = num;
          fulltime = false;
        }
      }
      else if (!frmLogin.GAuto.Settings.Account.IsLoginGGold)
        currentDuration += packageMS;
    }
    return currentDuration;
  }

