  private void ExtendTinhnang(
    int buySlotTime,
    int buySlotCount,
    string buyKey,
    bool useThread,
    bool defaultYes)
  {
    bool flag1 = false;
    if (frmLogin.GlobalTimer.ElapsedMilliseconds >= this.stampExtendTinhnang)
    {
      this.stampExtendTinhnang = frmLogin.GlobalTimer.ElapsedMilliseconds + 5000L;
      flag1 = true;
    }
    if (flag1)
    {
      if (frmLogin.GAuto.Settings.Account.MyLicense.CountUnit(buyKey) <= 0)
      {
        if (frmLogin.GAuto.Settings.Account.BangGia.Count <= 0)
          return;
        PriceItem price = (PriceItem) null;
        for (int index = 0; index < frmLogin.GAuto.Settings.Account.BangGia.Count; ++index)
        {
          bool flag2 = false;
          if (buySlotCount == 0)
            flag2 = true;
          else if (frmLogin.GAuto.Settings.Account.BangGia[index].SlotCount == buySlotCount)
            flag2 = true;
          if (frmLogin.GAuto.Settings.Account.BangGia[index].Key == buyKey && frmLogin.GAuto.Settings.Account.BangGia[index].Slot == buySlotTime && flag2)
          {
            price = frmLogin.GAuto.Settings.Account.BangGia[index];
            break;
          }
        }
        if (price == null)
          GA.ShowMessage("Không tìm thấy gói giá hợp phù hợp. Liên hệ admin", "Không thấy giá", 15000);
        else if (price.Price <= frmLogin.GAuto.Settings.Account.TotalBalance)
        {
          DialogResult dialogResult;
          if (!defaultYes)
          {
            string str = "";
            if (price.Price <= 0.0)
              str = " (miễn phí)";
            dialogResult = MessageBox.Show($"Bạn sẽ tốn {price.PriceDisplay + frmLogin.GGUnit}{str} để mua thêm gói '{price.ItemDisplay}'.\n\nBạn có đồng ý không?", "Xác nhận", MessageBoxButtons.YesNo, MessageBoxIcon.Question);
          }
          else
            dialogResult = DialogResult.Yes;
          if (dialogResult != DialogResult.Yes)
            return;
          frmLogin.frmBuyLicense.BuyTinhNang(buyKey, price, useForm: useThread);
        }
        else
        {
          string str = string.Format("Tài khoản của bạn còn {0} {1}, không đủ để mua thêm '{2}'.\n\nNạp thêm {1} vào tài khoản rồi thử lại", (object) frmLogin.GAuto.Settings.Account.TotalBalance.ToString("0.0"), (object) frmLogin.GGUnit, (object) price.ItemDisplay);
          if (!defaultYes)
            GA.ShowMessage(str, "Không đủ " + frmLogin.GGUnit, 15000);
          else
            GA.WriteUserLog(str);
        }
      }
      else if (frmLogin.CompilingLanguage == "VN")
        frmMain.OpenFrmLicenseNew();
      else
        frmMain.MyMuaGioForm();
    }
    else
      GA.ShowMessage("Bạn mua giờ nhanh quá, thử lại sau 5s", "Not OK");
  }

  private void ExtendYTO(int price, int hour, bool ask = true)
  {
    bool flag1 = false;
    if (frmLogin.GAuto.Settings.YTOStamp == 0L)
      flag1 = true;
    if (frmLogin.GAuto.Settings.YTOStamp > 0L && frmLogin.GlobalTimer.ElapsedMilliseconds > frmLogin.GAuto.Settings.YTOStamp)
      flag1 = true;
    if (flag1)
    {
      frmLogin.GAuto.Settings.YTOStamp = frmLogin.GlobalTimer.ElapsedMilliseconds + 120000L;
      if (frmLogin.CompilingLanguage == "VN")
      {
        switch (hour)
        {
          case 1:
            price = 2;
            break;
          case 3:
            price = 5;
            break;
        }
      }
      int num1 = (frmLogin.GAuto.Settings.YTOCounts ^ 2716) / 147;
      int currentDuration = (frmLogin.GAuto.Settings.YTODuration ^ 2716) / 147;
      bool fulltime = false;
      DateTime dateTime = DateTime.Now.Add(TimeSpan.FromSeconds((double) frmMain.GetSessionExpire(currentDuration, out fulltime, "yto", hour * 3600)));
      bool flag2 = false;
      if (ask)
      {
        DialogResult dialogResult;
        if (!fulltime)
        {
          frmLogin.GAuto.ConfirmBoxResult = DialogResult.No;
          int num2 = (int) new frmGGConfirm()
          {
            textQuestion = string.Format(frmMain.langYTOBuyHour, (object) hour.ToString(), (object) price, (object) dateTime.ToString(), (object) frmLogin.GAuto.Settings.ExpireString)
          }.ShowDialog();
          dialogResult = frmLogin.GAuto.ConfirmBoxResult;
        }
        else if (frmLogin.CompilingLanguage == "VN")
        {
          if (num1 >= 2)
            price *= num1 / 2;
          else
            price = price;
          int num3 = num1;
          if (num1 == 0)
            num3 = 2;
          dialogResult = MessageBox.Show(string.Format(frmMain.langYTOQuestion1, (object) price, (object) num3, (object) dateTime.ToString()), string.Format(frmMain.langQ12BuyHour, (object) hour.ToString()), MessageBoxButtons.YesNo, MessageBoxIcon.Question);
        }
        else
          dialogResult = MessageBox.Show(string.Format(frmMain.langYTOQuestion1, (object) price, (object) hour, (object) dateTime.ToString()), string.Format(frmMain.langYTOBuyHour2, (object) hour.ToString()), MessageBoxButtons.YesNo, MessageBoxIcon.Question);
        if (dialogResult == DialogResult.Yes)
          flag2 = true;
      }
      else
        flag2 = true;
      if (!flag2)
        return;
      frmLogin.GAuto.Settings.YTOExtend = hour;
      if (num1 == 0)
        num1 = 1;
      frmLogin.GAuto.Settings.YTORequest = num1;
      string version = "";
      string myMessage = "";
