  public void NVYenTuO(AutoAccount account)
  {
    if (account == null || !frmLogin.GAuto.Settings.IsPro1 || !this.cboxIsYTO.Focused && !account.MyFlag.DatLichFlag)
      return;
    account.Myself.isYTO = !account.Myself.isYTO;
    this.lvAllAccounts.Focus();
    if (account.Myself.isYTO)
    {
      if (account.Target.SubVersion == 4 && frmLogin.GAuto.Settings.EnableYTO == 0)
      {
        account.Myself.isYTO = false;
        this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + account.Myself.Name, frmMain.langDisableTinhNang, ToolTipIcon.Info);
      }
      else
      {
        string str = "tnyto";
        bool flag = true;
        int num1 = frmLogin.GAuto.Settings.Account.MyLicense.CountUnit(str);
        int currentIsTn = frmMain.GetCurrentIsTN(account, str);
        if (currentIsTn >= num1)
          flag = false;
        int num2 = 0;
        int num3 = num1;
        if (!GA.isVipMember())
        {
          try
          {
            for (int index = frmLogin.GAuto.AllAutoAccounts.Count - 1; index >= 0; --index)
            {
              if (frmLogin.GAuto.AllAutoAccounts[index].Myself.isYTO && frmLogin.GAuto.AllAutoAccounts[index].Myself.DatabaseID != account.Myself.DatabaseID)
              {
                ++num2;
                if (num2 >= num3)
                {
                  flag = false;
                  break;
                }
              }
            }
          }
          catch (Exception ex)
          {
          }
        }
        if (!flag)
        {
          if (frmLogin.CompilingLanguage == "EN" && num1 == 1)
          {
            account.Myself.isYTO = false;
            account.MyFlag.DatLichFlag = false;
            this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + account.Myself.Name, "You can only auto Swallow Deck from 2 character", ToolTipIcon.Info);
            return;
          }
          account.MyFlag.waitMsgBox = true;
          bool fulltime = false;
          int price = 2;
          if (frmLogin.CompilingLanguage != "VN")
            price = frmLogin.GAuto.Settings.Account.Price1HQuest;
