          DateTime.Now.Add(tempSpan);
          frmMain.CalcQuestPrice(ref price, ref tempSpan, "YTO");
          this.ExtendTinhnang(1, 0, str, false, account.MyFlag.DatLichFlag);
          if (frmLogin.GAuto.Settings.Account.MyLicense.CountUnit(str) > currentIsTn)
            flag = true;
          account.MyFlag.waitMsgBox = false;
        }
        if (!flag)
        {
          account.Myself.isYTO = false;
          this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + account.Myself.Name, frmMain.langYTONoGG, ToolTipIcon.Info);
        }
        else
        {
          account.MyFlag.DatLichFlag = false;
          if (!flag)
            return;
          account.Myself.isYTO = frmMain.DisableNhiemVu(account);
          account.IsAIEnabled = true;
          account.Myself.InMapPathPoints.Clear();
