          account.Myself.actionFlag = 0;
          account.Myself.QuestStep = 0;
          account.MyQuai.FirstAttackArray.Clear();
          account.MyFlag.phubanFlag = 0;
          account.Myself.NhiemVuQuaiID = -1;
          account.MyFlag.dosomethingCounter = 0;
          account.MyFlag.dosomethingFlag = 0;
          account.MyFlag.YTOCounter = 0;
          account.Myself.Status = AllEnums.CharStatuses.IDLE;
          account.Settings.AIMode = AllEnums.AIModes.NHIEMVU;
          account.Myself.MoveAcrossMap = false;
          account.Myself.MoveLongTrigger = false;
