  public static int GetCurrentIsTN(AutoAccount account, string tnkey)
  {
    int currentIsTn = 0;
    try
    {
      for (int index = 0; index < frmLogin.GAuto.AllAutoAccounts.Count; ++index)
      {
        bool flag = false;
        if (tnkey == "tnyto" && frmLogin.GAuto.AllAutoAccounts[index].Myself.isYTO)
          flag = true;
        if (tnkey == "tnq12" && (frmLogin.GAuto.AllAutoAccounts[index].Myself.IsQ1 || frmLogin.GAuto.AllAutoAccounts[index].Myself.IsQ2))
          flag = true;
        if (tnkey == "tnchedo" && frmLogin.GAuto.AllAutoAccounts[index].Myself.IsCheDo)
          flag = true;
        if (tnkey == "tntrader" && frmLogin.GAuto.AllAutoAccounts[index].Settings.AIMode == AllEnums.AIModes.THUONGNHAN)
          flag = true;
        if (frmLogin.GAuto.AllAutoAccounts[index] != null && frmLogin.GAuto.AllAutoAccounts[index].Myself != null && flag && frmLogin.GAuto.AllAutoAccounts[index].Myself.DatabaseID != account.Myself.DatabaseID)
          ++currentIsTn;
      }
    }
    catch (Exception ex)
    {
    }
    return currentIsTn;
  }

