  public bool OpenAutoPK(AutoAccount account, bool checkbox, bool menu = false)
  {
    if (frmLogin.GAuto.CurrentAuto != null && (this.cboxAutoPK.Focused || menu))
    {
      if (frmLogin.GAuto.CurrentAuto.Target.VersionNum == 3 && frmLogin.GAuto.CurrentAuto.Target.SubVersion == 4)
      {
        GA.ShowBalloon(frmMain.langAutoPKDisable, "", frmLogin.GAuto.CurrentAuto);
        checkbox = false;
        return false;
      }
      if (frmLogin.GAuto.Settings.Account.RemainMSeconds / 1000.0 / 60.0 / 60.0 + 1538.0 < 2496.0 && checkbox)
      {
        GA.ShowMessage(string.Format(frmMain.langFeatureLimitBalance, (object) 40), frmLogin.GAuto.CurrentAuto.Myself.Name, 30000);
        checkbox = false;
        return checkbox;
      }
      int num1 = 0;
      int num2 = (frmLogin.GAuto.Settings.TotalPKLimit ^ 1896) / 541;
      if (!GA.isVipMember())
      {
        try
        {
          for (int index = frmLogin.GAuto.AllAutoAccounts.Count - 1; index >= 0; --index)
          {
            if (frmLogin.GAuto.AllAutoAccounts[index].Settings.cboxPKEnable && frmLogin.GAuto.AllAutoAccounts[index].Myself.DatabaseID != frmLogin.GAuto.CurrentAuto.Myself.DatabaseID)
            {
              ++num1;
              if (num1 >= num2)
                break;
            }
          }
        }
        catch (Exception ex)
        {
        }
      }
      if (checkbox && num1 < num2 || !checkbox)
        frmLogin.GAuto.CurrentAuto.Settings.cboxPKEnable = checkbox;
      if (checkbox && num1 >= num2)
      {
        checkbox = false;
        this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + frmLogin.GAuto.CurrentAuto.Myself.Name, frmMain.langFeatureLimit, ToolTipIcon.Info);
      }
      this.lvAllAccounts.Focus();
    }
    return checkbox;
  }

