    if (frmLogin.GAuto.AllAutoAccounts.Count <= 0)
      return;
    if (frmLogin.GAuto.CurrentAuto == null)
      return;
    try
    {
      this.notifyIcon.ShowBalloonTip(5000, frmMain.langGroupID + frmLogin.GAuto.CurrentAuto.Settings.numGroupID.ToString(), frmMain.langGroupOffSkills, ToolTipIcon.Info);
      for (int index = frmLogin.GAuto.AllAutoAccounts.Count - 1; index >= 0; --index)
      {
        AutoAccount allAutoAccount = frmLogin.GAuto.AllAutoAccounts[index];
        if (allAutoAccount.Settings.numGroupID == frmLogin.GAuto.CurrentAuto.Settings.numGroupID)
          allAutoAccount.Settings.cboxSkillOnOff = false;
      }
    }
    catch (Exception ex)
    {
      GA.WriteUserLog(frmMain.langPleaseClick + this.btnSkills.Text + frmMain.lang1MoreTime, frmLogin.GAuto.CurrentAuto);
    }
  }

