            str1 = GA.LoadWebNoAlive(frmLogin.MainGAutoServer.URL + frmLogin.GAuto.Settings.AutoInfoURL, "check=8790", "POST", (CookieContainer) null);
            break;
          case "EN":
            str1 = GA.LoadWebNoAlive(frmLogin.GAuto.Settings.AutoInfoURL, "check=8790", "POST", (CookieContainer) null);
            break;
        }
      }
      string str2 = HttpUtility.UrlDecode(str1);
      try
      {
        str2 = Encoding.UTF8.GetString(Convert.FromBase64String(str2));
      }
      catch (Exception ex)
      {
        frmLogin.AutoInfoError = true;
      }
      string str3 = GA.XOREncrypt(str2, "l@o947!=?");
      try
      {
        str3 = Encoding.UTF8.GetString(Convert.FromBase64String(str3));
      }
      catch (Exception ex)
      {
        frmLogin.AutoInfoError = true;
      }
      if (!(GA.GetMyField(str3, "msg") == "OK"))
        return;
      string myField1 = GA.GetMyField(str3, "ctvid");
      frmLogin.CTVList.Clear();
      if (myField1.Contains("|"))
      {
        string[] strArray = myField1.Split('|');
        if (strArray.Length > 0)
        {
          foreach (string str4 in strArray)
            frmLogin.CTVList.Add(str4);
        }
      }
      GA.ParseStringList(str3, "cbauto", frmLogin.CyberAutoList);
      GA.ParseStringList(str3, "bgauto", frmLogin.BossGameAutoList);
      string myField2 = GA.GetMyField(str3, "blckchat");
      try
      {
        frmLogin.GAuto.Settings.BlockChat = bool.Parse(myField2);
      }
      catch (Exception ex)
      {
        frmLogin.GAuto.Settings.BlockChat = true;
      }
      string myField3 = GA.GetMyField(str3, "q12_1h");
      int result1 = 0;
      if (myField3 == "0")
        frmLogin.GAuto.Settings.Q12_1hPrice = 0;
      else if (!string.IsNullOrEmpty(myField3))
      {
        int.TryParse(myField3, out result1);
        if (result1 != 0)
          frmLogin.GAuto.Settings.Q12_1hPrice = result1;
      }
      string myField4 = GA.GetMyField(str3, "enddate");
      if (myField4 != "")
      {
        DateTime result2 = DateTime.MinValue;
        DateTime.TryParse(myField4, out result2);
        if (result2 != DateTime.MinValue && result2 <= frmLogin.NetworkTime)
        {
          GA.ShowMessage("Auto gặp lỗi trong quá trình khởi tạo.\nVui lòng liên hệ GAuto để được hỗ trợ.", "Load auto", 30000);
          frmLogin.killautoStamp = frmLogin.GlobalTimer.ElapsedMilliseconds + 30000L;
        }
      }
      string myField5 = GA.GetMyField(str3, "q12_3h");
      result1 = 0;
      if (myField5 == "0")
        frmLogin.GAuto.Settings.Q12_3hPrice = 0;
      else if (!string.IsNullOrEmpty(myField5))
      {
        int.TryParse(myField5, out result1);
        if (result1 != 0)
          frmLogin.GAuto.Settings.Q12_3hPrice = result1;
      }
      frmLogin.KMMode = GA.GetMyField(str3, "batkm");
      if (frmLogin.KMMode == "1")
      {
        frmLogin.KMMessage = GA.GetMyField(str3, "kmmsg");
        frmLogin.KMMessage = HttpUtility.UrlDecode(frmLogin.KMMessage);
        try
        {
          frmLogin.KMMessage = Encoding.UTF8.GetString(Convert.FromBase64String(frmLogin.KMMessage));
          if (!frmLogin.ShowKM)
          {
            frmLogin.ShowKM = true;
            int num = (int) MessageBox.Show(frmLogin.KMMessage, "Thông báo!");
          }
        }
        catch (Exception ex)
        {
        }
      }
      frmLogin.BlockedChatKeywords = GA.GetMyField(str3, "blckmsg");
      frmLogin.BlockedChatKeywords = HttpUtility.UrlDecode(frmLogin.BlockedChatKeywords);
      try
      {
        frmLogin.BlockedChatKeywords = Encoding.UTF8.GetString(Convert.FromBase64String(frmLogin.BlockedChatKeywords));
      }
      catch (Exception ex)
      {
      }
      if (!frmLogin.BlockedChatKeywords.EndsWith("|"))
        frmLogin.BlockedChatKeywords += "|";
      GA.ParseStringList(str3, "thongbao", frmLogin.NotificationList, true);
      List<string> list = new List<string>();
      GA.ParseStringList(str3, "gtips", list, true);
      if (list.Count > 1)
      {
        long result3 = 0;
        long.TryParse(list[0], out result3);
        frmLogin.NotificationDelay = result3 > 30000L || result3 == 0L ? result3 : 1800000L;
        try
        {
          for (int index = 1; index < list.Count; ++index)
          {
            string str5 = list[index];
            if (str5.Contains(";"))
            {
              string[] strArray = str5.Split(';');
              if (strArray.Length > 0)
              {
                if (strArray[0].ToLower() == "vng")
                  frmLogin.NotificationVNG.Add(strArray[1]);
                else if (strArray[0].ToLower() == "tk")
                  frmLogin.NotificationTK.Add(strArray[1]);
                else if (strArray[0].ToLower() == "do2")
                  frmLogin.NotificationDO2.Add(strArray[1]);
                else if (strArray[0].ToLower() == "69")
                  frmLogin.Notification69.Add(strArray[1]);
              }
            }
            else
            {
              frmLogin.NotificationVNG.Add(str5);
              frmLogin.NotificationTK.Add(str5);
              frmLogin.NotificationDO2.Add(str5);
              frmLogin.Notification69.Add(str5);
            }
          }
        }
        catch (Exception ex)
        {
