  public void NVTanThu(AutoAccount account)
  {
    if (account == null || !frmLogin.GAuto.Settings.IsPro1 || !this.cboxTanThu.Focused && !account.MyFlag.DatLichFlag)
      return;
    if (account.MyFlag.DatLichFlag)
      account.MyFlag.DatLichFlag = false;
    else if (account.Myself.Level >= 28)
      account.Myself.QuestStep = 20;
    account.Myself.isTanThu = !account.Myself.isTanThu;
    this.lvAllAccounts.Focus();
    if (account.Myself.isTanThu)
    {
      account.Myself.isTanThu = frmMain.DisableNhiemVu(account);
      account.IsAIEnabled = true;
      account.Myself.Status = AllEnums.CharStatuses.IDLE;
      account.Settings.AIMode = AllEnums.AIModes.NHIEMVU;
      account.Myself.MoveAcrossMap = false;
      account.Myself.MoveLongTrigger = false;
      account.Myself.IsLongMove = false;
      account.ResetEventString();
      account.ResetPacketMsg();
      account.Settings.cboxTuUpLevel = true;
      account.Settings.numUpLevel = 49;
      account.MyFlag.counter = 0;
      this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + account.Myself.Name, frmMain.langNhiemVuTanThuStart, ToolTipIcon.Info);
    }
    else
    {
      account.CallMoveFlashPos((int) account.Myself.PosX, (int) account.Myself.PosY);
      account.Myself.Status = AllEnums.CharStatuses.IDLE;
      account.IsAIEnabled = false;
      this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + account.Myself.Name, frmMain.langNhiemVuTanThuDeny, ToolTipIcon.Info);
    }
  }

