  private void MuaDoDiTrain(AutoAccount account)
  {
    if (account == null || account.Myself == null || !frmLogin.GAuto.Settings.IsPro1)
      return;
    if (account.Target.VersionNum == 3)
    {
      if (account.Myself.IsPartyFollowed == (byte) 1)
      {
        account.CallRemovePartyFollow();
        Thread.Sleep(500);
      }
      account.Myself.isMuaDoTrain = true;
    }
    else
    {
      if (account.Myself.IsPartyFollowed == (byte) 1)
      {
        account.CallRemovePartyFollow();
        Thread.Sleep(500);
      }
      if (!account.IsAIEnabled)
        account.IsAIEnabled = true;
      account.Myself.SavedStatus = AllEnums.CharStatuses.VETHANH;
      account.Myself.Status = AllEnums.CharStatuses.VETHANH;
      account.Myself.isMuaDoTrain = true;
      if (account.Settings.cboxTheoSau)
      {
        account.Myself.actionFlag = 1;
        account.Settings.cboxTheoSau = false;
      }
      this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + account.Myself.Name, frmMain.langBuySupplies, ToolTipIcon.Info);
    }
  }

