  public static bool InMyIgnoredList(int procID)
  {
    bool flag = false;
    if (frmLogin.GAuto.Settings.ProcessListIgnored.Count > 0)
    {
      try
      {
        for (int index = frmLogin.GAuto.Settings.ProcessListIgnored.Count - 1; index >= 0; --index)
        {
          if (frmLogin.GAuto.Settings.ProcessListIgnored[index].processID == procID)
          {
            flag = true;
            break;
          }
        }
      }
      catch (Exception ex)
      {
      }
    }
    return flag;
  }

  private void xóaDanhSáchProcessToolStripMenuItem_Click(object sender, EventArgs e)
  {
    if (frmLogin.GAuto.Settings.ProcessListIgnored.Count > 0)
    {
      if (frmLogin.GAuto.BlockedAutoAccounts.Count > 0)
      {
        for (int index = frmLogin.GAuto.BlockedAutoAccounts.Count - 1; index >= 0; --index)
        {
          frmLogin.GAuto.AllAutoAccounts.Add(frmLogin.GAuto.BlockedAutoAccounts[index]);
          frmLogin.GAuto.BlockedAutoAccounts[index].Target.TempRemoved = false;
          frmLogin.GAuto.BlockedAutoAccounts.RemoveAt(index);
        }
      }
      frmLogin.GAuto.Settings.ProcessListIgnored.Clear();
    }
    GA.WriteUserLog(frmMain.langProcessesDeleted);
  }

