  public void NVBuonDuaHau(AutoAccount account)
  {
    if (account == null || !frmLogin.GAuto.Settings.IsPro1 || !this.cboxBuonDuaHau.Focused && !account.MyFlag.HotkeyFlag && !account.MyFlag.DatLichFlag)
      return;
    account.MyFlag.HotkeyFlag = false;
    account.MyFlag.DatLichFlag = false;
    account.Myself.isBuonDuaHau = !account.Myself.isBuonDuaHau;
    this.lvAllAccounts.Focus();
    if (account.Myself.isBuonDuaHau)
    {
      account.Myself.isBuonDuaHau = frmMain.DisableNhiemVu(account);
      account.IsAIEnabled = true;
      account.Myself.Status = AllEnums.CharStatuses.IDLE;
      account.Settings.AIMode = AllEnums.AIModes.NHIEMVU;
      account.Myself.MoveAcrossMap = false;
      account.Myself.MoveLongTrigger = false;
      account.Myself.IsLongMove = false;
      account.CallResetNhiemVuData();
      account.Myself.QuestStep = 0;
      account.Myself.actionCounter = 0;
      this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + account.Myself.Name, frmMain.langStart + frmMain.langDuaHau, ToolTipIcon.Info);
    }
    else
    {
      account.CallMoveFlashPos((int) account.Myself.PosX, (int) account.Myself.PosY);
      account.Myself.Status = AllEnums.CharStatuses.IDLE;
      account.IsAIEnabled = false;
      this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + account.Myself.Name, frmMain.langStop + frmMain.langDuaHau, ToolTipIcon.Info);
    }
  }

