  private void ThangCheDoBuySlot(int slot)
  {
    if (frmLogin.GAuto.CurrentAuto == null)
      return;
    int num1 = (frmLogin.GAuto.Settings.CheDoCounts ^ 2013) / 152;
    int num2 = num1 + slot;
    int num3 = 2;
    switch (slot)
    {
      case 2:
        num3 = frmLogin.GAuto.Settings.ThangCheDo2AccPrice / 1000;
        break;
      case 5:
        num3 = frmLogin.GAuto.Settings.ThangCheDo5AccPrice / 1000;
        break;
      case 50:
        num3 = frmLogin.GAuto.Settings.ThangCheDo50AccPrice / 1000;
        break;
    }
    int currentDuration = frmMain.CalcCheDoDuration();
    bool fulltime = false;
    DateTime dateTime = DateTime.Now.Add(TimeSpan.FromSeconds((double) frmMain.GetSessionExpire(currentDuration, out fulltime)));
    if (MessageBox.Show(string.Format("- Bạn đang có: {0} slot chế đồ\n- Bạn mua thêm: {1} slot\n- Giá: {3} GG\n- Sử dụng đến: {4}\nNếu hạn dùng auto hết trước thì tính năng cũng sẽ hết theo\nBạn có đồng ý không?", (object) num1, (object) slot, (object) num2, (object) num3, (object) dateTime.ToString()), "THÁNG CHẾ ĐỒ - Mua thêm slot", MessageBoxButtons.YesNo, MessageBoxIcon.Question) != DialogResult.Yes)
      return;
    frmLogin.GAuto.Settings.BuyCheDo = true;
    frmLogin.GAuto.Settings.BuyCheDoCount = slot;
    string version = "";
    string myMessage = "";
    GA.SendKeepAlivePHP(out version, out myMessage);
  }

  private static int CalcCheDoDuration() => (frmLogin.GAuto.Settings.CheDoDuration ^ 2013) / 152;

