  public void NVPMP(AutoAccount account)
  {
    if (account == null || !frmLogin.GAuto.Settings.IsPro1 || !this.cboxPMP.Focused && !account.MyFlag.DatLichFlag)
      return;
    account.MyFlag.DatLichFlag = false;
    account.Myself.isPMP = !account.Myself.isPMP;
    this.lvAllAccounts.Focus();
    if (account.Myself.isPMP)
    {
      account.Myself.isPMP = frmMain.DisableNhiemVu(account);
      account.IsAIEnabled = true;
      account.Myself.Status = AllEnums.CharStatuses.IDLE;
      account.Settings.AIMode = AllEnums.AIModes.NHIEMVU;
      account.Myself.MoveAcrossMap = false;
      account.Myself.MoveLongTrigger = false;
      account.Myself.IsLongMove = false;
      account.Myself.InPathPointIndex = 0;
      account.Myself.InMapPathPoints.Clear();
      account.Myself.indexPPcalc = true;
      account.ResetPacketMsg();
      account.MyFlag.phubanFlag = 0;
      account.Myself.actionFlag = 0;
      account.MyFlag.seenTangThoCongStamp = 0L;
      account.NPCNhiemVu.Clear();
      this.notifyIcon.ShowBalloonTip(5000, frmMain.langCharacter + account.Myself.Name, frmMain.langStart + "auto PMP", ToolTipIcon.Info);
    }
