  private void phùVềThànhToolStripMenuItem_Click(object sender, EventArgs e)
  {
    List<AutoAccount> currentAccounts = this.GetCurrentAccounts();
    if (currentAccounts.Count <= 0)
      return;
    foreach (AutoAccount autoAccount in currentAccounts)
    {
      if (!GA.isInCity(autoAccount.Myself.MapID))
      {
        int itemIndex = autoAccount.SearchDVP();
        if (itemIndex >= 0)
        {
          autoAccount.CallXuongNgua();
          if (autoAccount.Myself.IsPartyFollowed == (byte) 1)
          {
            autoAccount.CallRemovePartyFollow();
            Thread.Sleep(1000);
          }
          autoAccount.CallUseItem(itemIndex, autoAccount.Myself.ID);
          autoAccount.Myself.IDLEStamp = frmLogin.GlobalTimer.ElapsedMilliseconds + 10000L;
        }
        else
        {
          autoAccount.CallXuongNgua();
          autoAccount.CallAttackTarget(0, 22, 0, 0);
          autoAccount.Myself.IDLEStamp = frmLogin.GlobalTimer.ElapsedMilliseconds + 20000L;
        }
      }
    }
  }

